import React, {useEffect, useState} from 'react';
import {Link} from 'react-router-dom';

import api from '../../services/api';
import './donator.css';
import PersonToDonate from '../../compenents/personToDonate';
import avatar from '../../assets/blood-avatar-men.png';
import {FiEdit, FiLogOut} from 'react-icons/fi';
import {BiDonateBlood} from 'react-icons/bi';

function Donator() {

    const persons = [
        {
            id: 1,
            name: "Denys",
            age: 21,
            reason: "cirurgia",
            blood: "O+",
            gender: "M"
        },
        {
            id: 2,
            name: "Pedro",
            age: 31,
            reason: "transfusão de sangue",
            blood: "A+",
            gender: "M"
        },
        {
            id: 3,
            name: "Maria",
            age: 25,
            reason: "cirurgia",
            blood: "O-",
            gender: "F"
        },
        {
            id: 4,
            name: "Jaquellyne",
            age: 21,
            reason: "cirurgia",
            blood: "A+",
            gender: "F"
        }
    ]
    const [info, setInfo] = useState('');
    const donatorId = localStorage.getItem('donatorId');
    const [patients, setPatients] = useState([]);

    useEffect(() => {
        api.get('donator', { 
            headers: { 
                Authorization: donatorId
            }
        }).then(response => {
            setInfo(response.data);
        });
    }, [donatorId]);

   useEffect()
    console.log(info);

    const user = { 
        "name": "Denys Menfredy",
        "doacoes": 0,
    }

    function swapDonate() {
        const donateBtn = document.getElementsByClassName("donate-btns");
        console.log(donateBtn);
    }

    return (
        <section className="container">
            <aside className="left-bar">
                <div className="user-info">
                    <img src={avatar} alt="blood avatar " />
                    <h4>{info.nome}</h4>
                    <h5>Tipo sanguineo: {info.tipo_sanguineo}</h5>
                    <span>{user.doacoes} doações realizadas</span>
                </div>
                <div className="divisor" />
                <nav className="aside-menu">
                    <ul>
                        <li>
                            <BiDonateBlood size="20px" /> 
                            <a href="#">Minhas doações</a>
                        </li>
                        <li>
                            <FiEdit size="20px" />
                            <a href="#">Editar perfil</a>
                        </li>
                        <li>
                            <FiLogOut size="20px" />
                            <Link to="/">Sair da conta</Link>
                        </li>
                    </ul>
                </nav>
            </aside>
            <div className="right-content">
                <div className="donate-btns">
                    <button>Doar para paciente</button>
                   
                    <button>Doar para Banco de sangue</button>
                </div>
                <div className="donate-to-patient">
                    <div className="search-box">
                        <form>
                            <input type="text" placeholder="Informe o nome da pessoa que você deseja doar sangue"></input>
                            <button className="search-btn">Buscar</button>
                        </form>
                    </div>
                    <h1>Pacientes disponivéis para doação</h1>
                    <div className="search-results">
                        <PersonToDonate persons={persons}/>
                    </div>
                </div>

                <div className="donate-to-bank">
                    <h1>Pacientes disponivéis para doação</h1>
                    <div className="search-box">
                        <form>
                            <input type="text" placeholder="Informe o nome da pessoa que você deseja doar sangue"></input>
                            <button className="search-btn">Buscar</button>
                        </form>
                    </div>
                    {/* <BankToDonate component here */} 
                </div>
            </div>
        </section>
    );
}

export default Donator;
