import React, {useState} from 'react';

import api from '../../services/api';

import './cadastro.css';

import logoLarge from '../../assets/logo-large.png'

function Cadastro() {

    const [nome, setNome] = useState('');
    const [idade, setIdade] = useState('');
    const [sexo, setSexo] = useState('');
    const [username, setUsername] = useState('');
    const [senha, setSenha] = useState('');
    const [telefone, setTelefone] = useState('');
    const [tipo_sanguineo, setTipoSanguineo] = useState('');

    async function handleCadastro(e) {
        e.preventDefault();
        try { 
            const response = await api.post('/donator', {
                nome, 
                idade, 
                sexo, 
                username,
                senha,
                telefone,
                tipo_sanguineo
            })
            const {id_doador} = api.post('/donator/getId', {username});
            console.log(id_doador);
        }catch(err) {

        }

    }

    return (
        <section className="cad-container">
            <div className="lh">
                <img src={logoLarge} alt="logo blood-donate" />
            </div>
            <div className="rh">
                <div className="cad-section">
                <h1>Crie uma conta agora e salve vidas!</h1>
                <form onSubmit={handleCadastro}>
                <h3>Informações Pessoais</h3>
                    <div input-name>
                        <input 
                            type="text" 
                            placeholder="insira seu nome"
                            value={nome}
                            onChange={setNome}
                         />
                    </div>

                    <div input-age>
                        <input 
                            type="number" 
                            placeholder="insira sua idade"
                            value={idade}
                            parse={value => !value ? null : Number(value)}
                            onChange={setIdade}
                         />
                    </div>

                    <div input-gender>
                        <label for="gender">Informe seu sexo</label> 
                            <select name="gender" id="gender" value={sexo} onchange={setSexo}>
                                <option value="M">Masculino</option>
                                <option value="F">Feminino</option>
                            </select>
                    </div>

                    <div input-blood>
                        <label for="blood">Informe seu tipo sanguineo</label> 
                            <select name="blood" id="blood" value={tipo_sanguineo} onChange={setTipoSanguineo}>
                                <option value="A+">A+</option>
                                <option value="A-">A-</option>
                                <option value="B+">B+</option>
                                <option value="B-">B-</option>
                                <option value="AB+">AB+</option>
                                <option value="AB-">AB-</option>
                                <option value="O+">O+</option>
                                <option value="O-">O-</option>
                            </select>
                    </div>
                    <h3>Informações de Login</h3>
                    <div input-username>
                        <input 
                            type="text" 
                            placeholder="insira seu nome de usuário"
                            value={username}
                            onChange={setUsername}
                         />
                    </div>
                    <div input-password>
                        <input 
                            type="text" 
                            placeholder="insira sua senha"
                         />
                    </div>
                    <button type="submit">Cadastrar-se</button>
                </form>
                </div>
            </div>
        </section>
    );

}


export default Cadastro;
